ports.body:
ports:
- name: ${name: Port Name}
  port: ${port: Port}
  protocol: TCP

volumeMounts.body:
volumeMounts:
  - name: ${name: Name of the Volume}
  mountPath: ${path: Path to mount}

volumes.body:
volumes:
  - name: ${name: name of the volume}
  mountPath: ${path: Path to mount}

volumes_hostPath.body:
volumes:
  - name: ${name: Volume Name}
  hostPath:
    path: ${path: Path to mount}
